<?phpclass LoginFilter extends Filter {	public $effect;	public $candirect = array("Default","Login","api") ;    public function execute ($filterChain) {    	$filterChain->execute();		return;	}    public function initialize ($context, $params = null) {		if($params['effect']){			$this->effect = true;		} else {			$this->effect = false;		}		parent::initialize($context, $params);		return true;    }}?>